-- Create ENUM type for job_type
-- CREATE TYPE IF NOT EXISTS job_type AS ENUM ('remote', 'full_time', 'contract');
DO $$ 
BEGIN
    IF NOT EXISTS (SELECT 1 FROM pg_type WHERE typname = 'job_type') THEN
        CREATE TYPE job_type AS ENUM ('remote', 'full_time', 'contract');
    END IF;
END $$;

DROP TABLE IF EXISTS joblisting;

-- Create table engine
CREATE TABLE IF NOT EXISTS joblisting (
